import time


class Solution:
    @staticmethod
    def convert(s: str, num_rows: int) -> str:
        length = len(s)
        if length <= 1 or length <= num_rows or num_rows <= 1:
            return s
        list0 = {}
        mark = 0
        increment = 1
        for i in range(length):
            if i < num_rows:
                list0[i] = s[i]
            else:
                list0[mark] += s[i]
            mark += increment
            if 0 < mark < num_rows - 1:
                continue
            else:
                increment = -increment
        return "".join(list(list0.values()))


