import math
import time

class Solution:
    def convert(self, s: str, numRows: int) -> str:
        length = len(s)
        if length<=1 or length <= numRows or numRows <=1:
            return s
        list0 = {}
        mark = 0
        increment = 1
        for i in range(length):
            if i < numRows:
                list0[i] = s[i]
            else:
                list0[mark] += s[i]
            mark += increment
            if 0 < mark < numRows-1:
                continue
            else:
                increment = -increment
        return "".join(list(list0.values()))


